import TodoInsert from "./components/TodoInsert";
import TodoTemplate from "./components/TodoTemplate";
import TodoList from "./components/TodoList";
import {useState, useRef, useCallback, useReducer} from 'react';
import {produce} from 'immer';

const createBulk = () => {
  const todoArray = [];

  for(let i=1;i<=2500;i++){
    const todo = {
      id: i,
      text: `할 일 ${i}`,
      checked: false
    }

    todoArray.push(todo);
  }

  return todoArray;
}

const reducer = (state, action)  => {
  switch(action.type) {
    case 'INSERT':
      return state.concat(action.todo);
    case 'REMOVE':
      return state.filter(todo=>todo.id!==action.id);
    case 'TOGGLE':

  }
}

function App() {
  const [todos, dispatch] = useReducer(createBulk);

  const todoId = useRef(2501);

  // 할 일을 추가하는 메소드
  const addTodo = useCallback((text) => {
    const newTodo = {
      id: todoId.current,
      text: text,
      checked: false,
    }

    setTodos(
      () => todos.concat(newTodo)
    );

    todoId.current += 1;
  }, [todos]);

  // 최적화2: setter 메소드의 매개변수로 화살표함수 전달.
  const removeTodo = useCallback((id) => {
    setTodos(
      () => todos.filter(todo=> todo.id!== id)
    );
  }, [todos]);

  // 최적화2: setter 메소드의 매개변수로 화살표함수 전달.
  const changeChecked = useCallback((id) => {
    setTodos(
      // setter 메소드에 화살표함수를 매개변수로 전달하면서
      // 화살표함수의 매개변수를 선언하면 해당 매개변수는
      // state의 이전상태를 가져오는 매개변수가 된다.
      (prev) => todos.map(
        todo => todo.id === id
        ? {...todo, checked: !todo.checked}
        : todo
      )
    )
  }, [todos]);

  return (
    <TodoTemplate>
      <TodoInsert addTodo={addTodo}/>
      <TodoList todos={todos} removeTodo={removeTodo} changeChecked={changeChecked}/>
    </TodoTemplate>
  );
}

export default App;
